    private void guardarArchivo(){
        try{
            //para guardar empleados en el dat
            FileOutputStream archivo = new FileOutputStream("habitaciones.dat");//vincula el archivo
                                
            ObjectOutputStream objetoSalida = new ObjectOutputStream(archivo);// captura el objeto a guardar
            
            for(int i=0;i<habitaciones.size();i++){
                objetoSalida.writeObject(habitaciones.get(i));//escribe uno a uno los objetos guardados en el array
                
            }
            objetoSalida.close();
            
            
        }
        catch(IOException e){
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
    }
    
    private void cargarArchivo(){
        habitaciones.clear();
        //leyendo las habitaciones que ya se habian cargado en el sistema
                
        try{
            //para leer archivos desde un .dat
            FileInputStream archivo = new FileInputStream("habitaciones.dat");// aqui abre el archivo
            ObjectInputStream objetoEntrada = new ObjectInputStream(archivo);// aqui recuper los objetos guardados en el archivo
            
            boolean finDeArchivo=false;
            while(!finDeArchivo){
                try{
                    habitaciones.add((Habitacion)objetoEntrada.readObject());
                }
                catch(EOFException e){
                    finDeArchivo=true;
                }
                catch(Exception f){
                       finDeArchivo=true;

//                    JOptionPane.showMessageDialog(null, "error 1");
                }
            }
            objetoEntrada.close();
        }
        catch(IOException e){
//            JOptionPane.showMessageDialog(null, "error 2");
        }
    }
    
    void agregarHabitacion(){
        Habitacion nuevahab= new Habitacion();//crea una nueva habitacion
        
        nuevahab.setEstado(false);
        nuevahab.setNumero(Integer.parseInt(txtNumHab.getText()));
        nuevahab.setPrecioxDia(Double.parseDouble(txtPrecioDia.getText()));
        nuevahab.setTipo((String) jComboBox1.getSelectedItem());
        
        habitaciones.add(nuevahab);
    }
    
    
    
    
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
           
        
        
        if(txtNumHab.getText().isEmpty()||txtPrecioDia.getText().isEmpty()|| jComboBox1.getSelectedItem().equals("-")){
        JOptionPane.showMessageDialog(null,"Completar todos los campos");
            
        }else{
        
            cargarArchivo();
            agregarHabitacion();
            guardarArchivo();  
        }
                  
        


        
        
//          mostrando habitaciones
          System.out.println("mostrando habitaciones guardadas");
            System.out.println(habitaciones.size());
            for(int i=0;i<habitaciones.size();i++){
            System.out.println(habitaciones.get(i).toString());    
            
                
            }
        this.setVisible(false);
        
        
    }//GEN-LAST:event_jButton1ActionPerformed
    
